import React, { useState } from 'react';

const VendorPage = () => {
  const [formData, setFormData] = useState({
    contactName: '',
    businessName: '',
    email: '',
    phoneNumber: '',
    website: '',
    productsServices: '',
    itemsList: '',
    electricityRequired: false,
    staffCount: 0,
    hasDigitalPayment: false,
    acceptedTerms: false,
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission, e.g., send data to server
    console.log(formData);
  };

  return (
    <div className="flex flex-col md:flex-row justify-center items-start gap-8">
      {/* Registration form column */}
      <div className="w-full md:w-1/2 p-6 rounded-lg shadow-lg">
        <h1 className="text-2xl font-bold mb-4">Registration Form</h1>
        <form onSubmit={handleSubmit}>
          {/* Contact Name */}
          {/* Business Name */}
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              CONTACT NAME
            </label>
            <input
              type="text"
              name="contactName"
              value={formData.contactName}
              onChange={handleChange}
              className="w-full border-gray-300 border rounded-md py-2 px-3 focus:outline-none focus:border-blue-500"
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              BUSINESS NAME
            </label>
            <input
              type="text"
              name="contactName"
              value={formData.contactName}
              onChange={handleChange}
              className="w-full border-gray-300 border rounded-md py-2 px-3 focus:outline-none focus:border-blue-500"
            />
          </div>
          <div className="flex flex-wrap -mx-2">
            {/* Email */}
            <div className="w-full md:w-1/2 px-2 mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                EMAIL
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full border-gray-300 border rounded-md py-2 px-3 focus:outline-none focus:border-blue-500"
              />
            </div>
            {/* Phone Number */}
            <div className="w-full md:w-1/2 px-2 mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                PHONE NUMBER
              </label>
              <input
                type="tel"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
                className="w-full border-gray-300 border rounded-md py-2 px-3 focus:outline-none focus:border-blue-500"
              />
            </div>
          </div>
          {/* Website */}
          {/* Products or Services */}
          {/* List of Items to be Sold */}
          {/* Electricity Required */}
          {/* Staff Count */}
          {/* Digital Payment System */}
          {/* Accept Terms and Conditions */}
          {/* Submit button */}
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              WEBSITE
            </label>
            <input
              type="url"
              name="website"
              value={formData.website}
              onChange={handleChange}
              className="w-full border-gray-300 border rounded-md py-2 px-3 focus:outline-none focus:border-blue-500"
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
            Please state the Products or Services you offer and List of items to be sold (Kindly note that only approved products and service as per agreement will be permitted at the event)
            </label>
            <textarea
              name="productsServices"
              value={formData.productsServices}
              onChange={handleChange}
              className="w-full border-gray-300 border rounded-md py-2 px-3 focus:outline-none focus:border-blue-500"
            />
          </div>
        
          <div className="flex flex-wrap -mx-2">
            
            <div className="w-full md:w-1/2 px-2 mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
            Do you require electricity at your stand
            </label>
            <textarea
              name="itemsList"
              value={formData.itemsList}
              onChange={handleChange}
              className="w-full border-gray-300 border rounded-md py-2 px-3 focus:outline-none focus:border-blue-500"
            />
            </div>
            
            <div className="w-full md:w-1/2 px-2 mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              How many staff will you bring?
            </label>
            <input
              type="number"
              name="staffCount"
              value={formData.staffCount}
              onChange={handleChange}
              className="w-full border-gray-300 border rounded-md py-2 px-3 focus:outline-none focus:border-blue-500"
            />
            </div>
          </div>
          
          {/* <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Do you require electricity at your stand?
            </label>
            <input
              type="checkbox"
              name="electricityRequired"
              checked={formData.electricityRequired}
              onChange={handleChange}
              className="mr-2 leading-tight"
            />
            <span className="text-sm text-gray-700">
              Yes, I require electricity
            </span>
          </div> */}

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Do you have a digital payment system for your business?
            </label>
            <input
              type="checkbox"
              name="hasDigitalPayment"
              checked={formData.hasDigitalPayment}
              onChange={handleChange}
              className="mr-2 leading-tight"
            />
            <span className="text-sm text-gray-700">
              Yes, I have a digital payment system
            </span>
          </div>
          <div className="mb-4">
            <label className="flex items-center">
              <input
                type="checkbox"
                name="acceptedTerms"
                checked={formData.acceptedTerms}
                onChange={handleChange}
                className="mr-2 leading-tight"
              />
              <span className="text-sm text-gray-700">
                I have read and accept the terms and conditions
              </span>
            </label>
          </div>
          <button
            type="submit"
            className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
          >
            Register
          </button>
        </form>
      </div>
      <div className="w-full md:w-1/2">
        <h1 className="text-3xl font-bold mb-4">Registration Details</h1>
        <p className="text-lg text-gray-700 leading-relaxed">
        Are you a food, drinks or merchandise vendor? Apply to join <br />us at the jollof summit and showcase your offerings to thousands of attendees!<br />
       Starting price: <br />jollof stand: 20.000 etb <br /> drink stand: 15.000 etb <br />non food/drink stand: 15.000 etb <br />
       Please note that the following resources will be provided to each vendor: <br />
       1. A stand (As applicable) <br />
       2. Table & Chairs (quantity dependent on type of stand)<br />
    4. Electricity (As applicable)<br />

  The above resources maybe subject to change based on the discretion of the organizers and each vendor’s special requirements.<br />
  Vendors will be allotted tickets for 3 staff members (Including lead vendor). Staff members exceeding the allotted number will attract a entry fee of 5.000 etb.< br/>
 We shall respond to the vendor applications within a maximum of 2 weeks. Should your application be successful, you would be sent a Vendor Agreement to review and sign in order for us to proceed.<br />
Thank you for your interest in participating in the Jollof Summit!<br /><br />
  Terms and Conditions 
        
  <br /> These Vendor Terms and Conditions (“Terms”) govern the relationship between the vendor (“Vendor”) and the Waakye Summit (“Organizer”). By submitting a vendor application, the Vendor agrees to comply with these Terms.<br />
1. Application Process:<br />
Submission of the Vendor Application Form does not guarantee participation. The Organizer reserves the right to accept or reject applications at its discretion. Vendors must provide accurate and complete information in the application.<br />
2. Stand Allocation:<br />
Stand allocation is at the sole discretion of the Organizer. Vendors will be notified of their stand allocation in advance of the event.<br />
3. Stand Fees:<br />
Stand fees must be paid in full by the specified deadline to secure participation. Fees are non-refundable, except at the discretion of the Organizer.<br />
4. Operational Requirements:<br />
Vendors are responsible for all aspects of their stand’s setup and operation. Stands must comply with safety and hygiene regulations.<br />
        </p>
      </div>
    </div>
  );
};

export default VendorPage;
