// src/components/Carousel.jsx
import React, { useState } from 'react';
import { Transition } from '@headlessui/react';

const Carousel = ({ images }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + images.length) % images.length);
  };

  return (
    <div className="max-w-screen-xl mx-auto">
      <div className="carousel relative shadow-2xl bg-white w-full">
        <div className="relative w-full">
          <Transition
            as={React.Fragment}
            show={true}
            enter="transition-opacity duration-1000"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="transition-opacity duration-1000"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <img src={images[currentSlide]} alt="Slide" className="object-cover w-full h-64" />
          </Transition>

          <div className="absolute inset-x-0 bottom-4 flex justify-center space-x-2">
            {images.map((_, index) => (
              <button
                key={index}
                className={`w-2 h-2 rounded-full ${index === currentSlide ? 'bg-black' : 'bg-gray-300'}`}
                onClick={() => setCurrentSlide(index)}
              ></button>
            ))}
          </div>

          <div className="absolute inset-y-0 left-0 ml-2 flex items-center justify-center">
            <button
              className="w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
              onClick={prevSlide}
            >
              &lt;
            </button>
          </div>

          <div className="absolute inset-y-0 right-0 mr-2 flex items-center justify-center">
            <button
              className="w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
              onClick={nextSlide}
            >
              &gt;
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Carousel;
