import React, { useState, useEffect } from 'react';
import { Transition } from '@headlessui/react';

const Carousel = ({ images }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [autoplay, setAutoplay] = useState(true);

  useEffect(() => {
    let intervalId;

    if (autoplay) {
      intervalId = setInterval(() => {
        setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
      }, 3000); // Adjust autoplay interval as needed
    }

    return () => clearInterval(intervalId);
  }, [autoplay, images.length]);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + images.length) % images.length);
  };

  return (
    <div className="max-w-screen-xl mx-auto">
      <div
        className="carousel relative shadow-2xl bg-white w-full"
        onMouseEnter={() => setAutoplay(false)}
        onMouseLeave={() => setAutoplay(true)}
      >
        <div className="relative w-full">
          {images.map((img, index) => (
            <Transition
              key={index}
              show={index === currentSlide}
              enter="transition-transform duration-1000 ease-in-out"
              enterFrom="translate-y-full opacity-0"
              enterTo="translate-y-0 opacity-100"
              leave="transition-transform duration-1000 ease-in-out"
              leaveFrom="translate-y-0 opacity-100"
              leaveTo="translate-y-full opacity-0"
            >
              <div className="h-64">
                <img src={img} alt={`Slide ${index + 1}`} className="object-cover w-full h-full" />
                <div className="absolute bottom-10 left-0 right-0 text-center">
                  <h1 className="text-3xl text-white font-bold">Heading 1</h1>
                  <h2 className="text-lg text-white font-semibold">Subheading 2</h2>
                  <button className="mt-4 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded">
                    Button
                  </button>
                </div>
              </div>
            </Transition>
          ))}

          <div className="absolute inset-x-0 bottom-4 flex justify-center space-x-2">
            {images.map((_, index) => (
              <button
                key={index}
                className={`text-lg ${index === currentSlide ? 'text-black' : 'text-gray-300'}`}
                onClick={() => setCurrentSlide(index)}
              >
                {index === currentSlide ? '—' : '—'}
              </button>
            ))}
          </div>

          <div className="absolute inset-y-0 left-0 ml-2 flex items-center justify-center">
            <button
              className="w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
              onClick={prevSlide}
            >
              &lt;
            </button>
          </div>

          <div className="absolute inset-y-0 right-0 mr-2 flex items-center justify-center">
            <button
              className="w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
              onClick={nextSlide}
            >
              &gt;
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Carousel;
