import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { FaInfoCircle, FaTicketAlt, FaStore, FaImages, FaVoteYea, FaPhoneAlt } from 'react-icons/fa'; // Import FontAwesome icons
import { faFacebook, faTwitter, faInstagram, faYoutube} from '@fortawesome/free-brands-svg-icons';
const Carousel = ({ images }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [autoplay, setAutoplay] = useState(true);
  const [showMenu, setShowMenu] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    let intervalId;

    if (autoplay) {
      intervalId = setInterval(() => {
        setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
      }, 4000);
    }

    return () => clearInterval(intervalId);
  }, [autoplay, images.length]);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + images.length) % images.length);
  };

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="w-full overflow-hidden relative">
      
       <div className="absolute bottom-24 right-4 flex flex-col items-end space-y-4 z-50">
        <a href="#" className="text-white"><FontAwesomeIcon icon={faFacebook} size="2x" /></a>
        <a href="#" className="text-white"><FontAwesomeIcon icon={faTwitter} size="2x" /></a>
        <a href="#" className="text-white"><FontAwesomeIcon icon={faInstagram} size="2x" /></a>
        <a href="#" className="text-white"><FontAwesomeIcon icon={faYoutube} size="2x" /></a>
      </div>
      {/* Menu Bar */}
      <div className={`fixed left-0 top-0 mt-6 w-64 bg-gray-700 flex items-center justify-center z-50 transition-transform duration-500 ${showMenu ? 'translate-x-0' : '-translate-x-full'}`} style={{ height: '570px' }}>
        <ul className="text-white">
          <li className="py-2 cursor-pointer flex items-center" onClick={() => scrollToSection('about')}>
            <FaInfoCircle className="mr-2" /> ABOUT US
          </li>
          <li className="py-2 cursor-pointer flex items-center" onClick={() => navigate('tickets')}>
            <FaTicketAlt className="mr-2" /> TICKETS
          </li>
          <li className="py-2 cursor-pointer flex items-center" onClick={() => navigate('/vendor')}>
            <FaStore className="mr-2" /> BECOME A VENDOR
          </li>
          <li className="py-2 cursor-pointer flex items-center" onClick={() => scrollToSection('gallery')}>
            <FaImages className="mr-2" /> GALLERY
          </li>
          <li className="py-2 cursor-pointer flex items-center" onClick={() => scrollToSection('vote')}>
            <FaVoteYea className="mr-2" /> VOTE
          </li>
          <li className="py-2 cursor-pointer flex items-center" onClick={() => scrollToSection('contact')}>
            <FaPhoneAlt className="mr-2" /> CONTACT
          </li>
        </ul>
      </div>

      {/* Carousel Content */}
      <div className="relative w-full">
        <div
          className="carousel relative shadow-2xl bg-white w-full h-full"
          onMouseEnter={() => setAutoplay(false)}
          onMouseLeave={() => setAutoplay(true)}
        >
          {/* Carousel Images */}
          <div className="relative w-full h-96 overflow-hidden" style={{ height: '600px' }}>
            {images.map((img, index) => (
              <div key={index} className={`absolute inset-0 w-full h-full transition-opacity duration-1000 ${index === currentSlide ? 'opacity-100' : 'opacity-0'}`}>
                <img
                  src={img}
                  style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                  alt={`Slide ${index + 1}`}
                />
                {/* Content overlay */}
                <div className="absolute inset-x-0 bottom-10 text-center z-20">
                <h3>JOLLOF SUMMIT</h3>
                  <h1 className={`text-3xl font-bold text-white transition-transform duration-1000 ${index === currentSlide ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
                    THE BIGGEST JOLLOF FESTIVAL <br /> in ADDIS-ABABA!
                  </h1>
                  <h6 className={`text-lg mt-6 font-semibold text-white transition-transform duration-1000 ${index === currentSlide ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
                    Jollof is more than just a rice dish - it’s <br /> the thread that ties diverse people together
                  </h6>
                  <div className='mt-64'>
                    <button
                      className={`mt-12 bg-slate-900 hover:bg-slate-950 text-white font-bold py-2 px-4 rounded transition-transform duration-1000 ${index === currentSlide ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}
                      onClick={() => navigate('/tickets')}
                    >
                      GET TICKETS HERE
                    </button>
                    <button
                      className={`mt-12 bg-slate-900 hover:bg-slate-950 text-white font-bold py-2 px-4 ml-4 rounded transition-transform duration-1000 ${index === currentSlide ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}
                      onClick={() => navigate('/vendor')}
                    >
                      BECOME A VENDOR
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
         
          {/* Navigation Button */}
      
          <div className="absolute inset-x-0 bottom-0 flex justify-end items-center p-4">
           
            <button
              className="w-8 h-8 bg-white bg-opacity-50 text-black rounded-full hover:bg-opacity-70"
              onClick={prevSlide}
              style={{ marginRight: '8px' }}
            >
              &#8592; 
            </button>
            <div className="flex space-x-2 mt-10">
              <div className={`text-white-800 h-4 hover:text-white-700 cursor-pointer ${currentSlide === 0 ? 'text-pink-800' : ''}`} onClick={() => setCurrentSlide(0)}>
                —
              </div>
              <div className={`text-white-800 hover:text-white-700 cursor-pointer ${currentSlide === 1 ? 'text-pink-800' : ''}`} onClick={() => setCurrentSlide(1)}>
                —
              </div>
              <div className={`text-white-800 hover:text-white-700 cursor-pointer ${currentSlide === 2 ? 'text-pink-800' : ''}`} onClick={() => setCurrentSlide(2)}>
                —
              </div>
            </div>

            <button
              className="w-8 h-8 bg-white bg-opacity-50 text-black rounded-full hover:bg-opacity-70"
              onClick={nextSlide}
            >
              &#8594; 
            </button>
          </div>
        </div>
      </div>

      {/* Menu Button */}
      <button
        className="fixed top-4 left-4 bg-gray-700 text-white rounded-full h-10 w-10 flex items-center justify-center z-50"
        onClick={() => setShowMenu((prev) => !prev)}
      >
        {showMenu ? 'X' : '☰'}
      </button>
      
     
    
    
     
      
     
    </div>
  );
};

export default Carousel;
