import React, { useState, useEffect } from 'react';

const Carousel = ({ images }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [autoplay, setAutoplay] = useState(true);

  useEffect(() => {
    let intervalId;

    if (autoplay) {
      intervalId = setInterval(() => {
        setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
      }, 3000); // Adjust autoplay interval as needed
    }

    return () => clearInterval(intervalId);
  }, [autoplay, images.length]);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + images.length) % images.length);
  };

  return (
    <div className="max-w-screen-xl mx-auto">
      <div
        className="carousel relative shadow-2xl bg-white w-full flex justify-center items-center"
        onMouseEnter={() => setAutoplay(false)}
        onMouseLeave={() => setAutoplay(true)}
      >
        <button
          className="absolute inset-y-0 left-0 ml-2 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
          onClick={prevSlide}
        >
          &lt;
        </button>

        <div className="relative w-full h-64 text-center">
          {images.map((img, index) => (
            <img
              key={index}
              src={img}
              alt={`Slide ${index + 1}`}
              className={`absolute inset-0 object-cover w-full h-full transition-opacity duration-1000 ${
                index === currentSlide ? 'opacity-100' : 'opacity-0'
              }`}
            />
          ))}

          <div className="absolute inset-0 flex flex-col justify-center items-center">
            <h1 className={`text-3xl font-bold text-white transition-transform duration-1000 ${currentSlide === 0 ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
              Heading 1
            </h1>
            <h2 className={`text-lg font-semibold text-white transition-transform duration-1000 ${currentSlide === 0 ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
              Subheading 2
            </h2>
            <button className={`mt-4 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition-transform duration-1000 ${currentSlide === 0 ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
              Button
            </button>
          </div>
        </div>

        <button
          className="absolute inset-y-0 right-0 mr-2 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
          onClick={nextSlide}
        >
          &gt;
        </button>
      </div>
    </div>
  );
};

export default Carousel;
