import React, { useState, useEffect } from 'react';

const Carousel = ({ images }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [autoplay, setAutoplay] = useState(true);

  useEffect(() => {
    let intervalId;

    if (autoplay) {
      intervalId = setInterval(() => {
        setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
      }, 3000); // Adjust autoplay interval as needed
    }

    return () => clearInterval(intervalId);
  }, [autoplay, images.length]);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + images.length) % images.length);
  };

  return (
    <div className="max-w-screen-xl mx-auto">
      <div
        className="carousel relative shadow-2xl bg-white w-full"
        onMouseEnter={() => setAutoplay(false)}
        onMouseLeave={() => setAutoplay(true)}
      >
        <div className="relative w-full h-64">
          {images.map((img, index) => (
            <img
              key={index}
              src={img}
              style={{ height: '80vh' }}
              alt={`Slide ${index + 1}`}
              className={`absolute inset-0 object-cover w-full h-full transition-opacity duration-1000 ${
                index === currentSlide ? 'opacity-100' : 'opacity-0'
              }`}
            />
          ))}
        </div>

        <div className="absolute inset-x-0 bottom-10 text-center">
          <h1 className={`text-3xl font-bold text-white transition-transform duration-1000 ${currentSlide === 0 ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
          THE BIGGEST JOLLOF FESTIVAL in ADDIS-ABABA! 
          </h1>
          <h6 className={`text-lg font-semibold text-white transition-transform duration-1000 ${currentSlide === 0 ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
          Jollof is more than just a rice dish - itâ€™s the thread that ties diverse people together
          </h6>
          <button className={`mt-4 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition-transform duration-1000 ${currentSlide === 0 ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
            Button
          </button>
          <button className={`mt-4 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 flex justify-between rounded transition-transform duration-1000 ${currentSlide === 0 ? 'translate-y-0 opacity-100' : 'translate-y-full opacity-0'}`}>
            Button
          </button>
        </div>

        <div className="absolute inset-y-0 left-0 ml-2 flex items-center justify-center h-96">
          <button
            className="w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
            onClick={prevSlide}
          >
            &lt;
          </button>
        </div>

        <div className="absolute inset-y-0 right-0 mr-2 flex items-center justify-center h-96">
          <button
            className="w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
            onClick={nextSlide}
          >
            &gt;
          </button>
        </div>
      </div>
    </div>
  );
};

export default Carousel;
