import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FaInfoCircle, FaTicketAlt, FaStore, FaImages, FaVoteYea, FaPhoneAlt } from 'react-icons/fa'; // Import FontAwesome icons
import { faFacebook, faTwitter, faInstagram, faYoutube } from '@fortawesome/free-brands-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const NavigationMenu = () => {
  const navigate = useNavigate();
  const [showMenu, setShowMenu] = useState(false);

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <>
      {/* Desktop Navigation */}
      <nav className="hidden sm:flex justify-end items-center">
        <ul className="flex space-x-6 text-white">
          <li className="cursor-pointer" onClick={() => scrollToSection('about')}>
            ABOUT US
          </li>
          <li className="cursor-pointer" onClick={() => navigate('tickets')}>
            TICKETS
          </li>
          <li className="cursor-pointer" onClick={() => navigate('/vendor')}>
            BECOME A VENDOR
          </li>
          <li className="cursor-pointer" onClick={() => scrollToSection('gallery')}>
            GALLERY
          </li>
          <li className="cursor-pointer" onClick={() => scrollToSection('vote')}>
            VOTE
          </li>
          <li className="cursor-pointer" onClick={() => scrollToSection('contact')}>
            CONTACT
          </li>
        </ul>
      </nav>

      {/* Mobile Navigation */}
      <div className="sm:hidden">
        <button
          className="text-white"
          onClick={() => setShowMenu((prev) => !prev)}
        >
          {showMenu ? 'Close Menu' : 'Open Menu'}
        </button>
        {showMenu && (
          <div className="fixed top-0 left-0 bg-gray-700 text-white w-full h-full flex flex-col items-center justify-center">
            <ul className="text-white">
              <li className="py-2 cursor-pointer" onClick={() => scrollToSection('about')}>
                <FaInfoCircle className="mr-2" /> ABOUT US
              </li>
              <li className="py-2 cursor-pointer" onClick={() => navigate('tickets')}>
                <FaTicketAlt className="mr-2" /> TICKETS
              </li>
              <li className="py-2 cursor-pointer" onClick={() => navigate('/vendor')}>
                <FaStore className="mr-2" /> BECOME A VENDOR
              </li>
              <li className="py-2 cursor-pointer" onClick={() => scrollToSection('gallery')}>
                <FaImages className="mr-2" /> GALLERY
              </li>
              <li className="py-2 cursor-pointer" onClick={() => scrollToSection('vote')}>
                <FaVoteYea className="mr-2" /> VOTE
              </li>
              <li className="py-2 cursor-pointer" onClick={() => scrollToSection('contact')}>
                <FaPhoneAlt className="mr-2" /> CONTACT
              </li>
            </ul>
          </div>
        )}
      </div>
    </>
  );
};

export default NavigationMenu;
