// src/components/Carousel.js
import React, { useState } from 'react';
import { Transition } from '@headlessui/react';

const Carousel = ({ images }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + images.length) % images.length);
  };

  return (
    <div className="relative">
      <Transition
        as={React.Fragment}
        show={true}
        enter="transition-opacity duration-1000"
        enterFrom="opacity-0"
        enterTo="opacity-100"
        leave="transition-opacity duration-1000"
        leaveFrom="opacity-100"
        leaveTo="opacity-0"
      >
        <img src={images[currentSlide]} alt="Slide" className="object-cover w-full h-64" />
      </Transition>

      <button
        className="absolute inset-y-0 left-0 ml-2 flex items-center justify-center w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
        onClick={prevSlide}
      >
        &lt;
      </button>

      <button
        className="absolute inset-y-0 right-0 mr-2 flex items-center justify-center w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
        onClick={nextSlide}
      >
        &gt;
      </button>
    </div>
  );
};

export default Carousel;
