import React, { useState, useEffect } from 'react';
import { Transition } from '@headlessui/react';

const Carousel = ({ images }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [autoplay, setAutoplay] = useState(true); // State to control autoplay

  useEffect(() => {
    let intervalId;

    if (autoplay) {
      intervalId = setInterval(() => {
        setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
      }, 3000); // Adjust autoplay interval as needed (e.g., every 3 seconds)
    }

    return () => clearInterval(intervalId); // Cleanup the interval on component unmount
  }, [autoplay, images.length]);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + images.length) % images.length);
  };

  return (
    <div className="max-w-screen-xl mx-auto">
      <div
        className="carousel relative shadow-2xl bg-white w-full"
        onMouseEnter={() => setAutoplay(false)} // Pause autoplay on hover
        onMouseLeave={() => setAutoplay(true)} // Resume autoplay on mouse leave
      >
        <div className="relative w-full">
          <Transition
            as={React.Fragment}
            show={true}
            enter="transition-opacity duration-1000"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="transition-opacity duration-1000"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <img
              src={images[currentSlide]}
              alt="Slide"
              className="object-cover w-full"
              style={{ height: '80vh' }}
            />
          </Transition>

          <div className="absolute inset-x-0 bottom-4 flex justify-center space-x-2">
            {images.map((_, index) => (
              <button
                key={index}
                className={`text-lg ${index === currentSlide ? 'text-black' : 'text-gray-300'}`}
                onClick={() => setCurrentSlide(index)}
              >
                {index === currentSlide ? '—' : '—'}
              </button>
            ))}
          </div>

          <div className="absolute inset-y-0 left-0 ml-2 flex items-center justify-center">
            <button
              className="w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
              onClick={prevSlide}
            >
              &lt;
            </button>
          </div>

          <div className="absolute inset-y-0 right-0 mr-2 flex items-center justify-center">
            <button
              className="w-10 h-10 bg-black bg-opacity-50 text-white rounded-full hover:bg-opacity-70"
              onClick={nextSlide}
            >
              &gt;
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Carousel;
