import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaInfoCircle, FaTicketAlt, FaStore, FaImages, FaVoteYea, FaPhoneAlt } from 'react-icons/fa';


const NavigationMenu = () => {
  const navigate = useNavigate();
  const [showMenu, setShowMenu] = useState(false);

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <>
      {/* Desktop Navigation */}
      <nav className="hidden sm:flex justify-end items-center">
        <ul className="flex space-x-6 text-white">
          <li className="cursor-pointer" onClick={() => scrollToSection('about')}>
            ABOUT US
          </li>
          <li className="cursor-pointer" onClick={() => navigate('tickets')}>
            TICKETS
          </li>
          <li className="cursor-pointer" onClick={() => navigate('/vendor')}>
            BECOME A VENDOR
          </li>
          <li className="cursor-pointer" onClick={() => scrollToSection('gallery')}>
            GALLERY
          </li>
          <li className="cursor-pointer" onClick={() => scrollToSection('vote')}>
            VOTE
          </li>
          <li className="cursor-pointer" onClick={() => scrollToSection('contact')}>
            CONTACT
          </li>
        </ul>
      </nav>

      {/* Mobile Navigation */}
      <div className="sm:hidden fixed top-0 left-0 w-full h-full z-50 transition-transform duration-300 ease-in-out" style={{ transform: showMenu ? 'translateX(0)' : 'translateX(-100%)' }}>
        <div className="bg-gray-700 text-white w-3/4 h-full flex flex-col items-center justify-center p-4">
          <button
            className="absolute top-4 left-4 text-white"
            onClick={() => setShowMenu(false)}
          >
            
          </button>
          <ul className="text-white ">
            <li className="py-2 cursor-pointer flex" onClick={() => scrollToSection('about')}>
              <FaInfoCircle className="mr-2" />ABOUT US
            </li>
            <li className="py-2 cursor-pointer flex" onClick={() => navigate('tickets')}>
              <FaTicketAlt className="mr-2" /> TICKETS
            </li>
            <li className="py-2 cursor-pointer flex" onClick={() => navigate('/vendor')}>
              <FaStore className="mr-2" /> BECOME A VENDOR
            </li>
            <li className="py-2 cursor-pointer flex" onClick={() => scrollToSection('gallery')}>
              <FaImages className="mr-2" /> GALLERY
            </li>
            <li className="py-2 cursor-pointer flex" onClick={() => scrollToSection('vote')}>
              <FaVoteYea className="mr-2" /> VOTE
            </li>
            <li className="py-2 cursor-pointer flex" onClick={() => scrollToSection('contact')}>
              <FaPhoneAlt className="mr-2" /> CONTACT
            </li>
          </ul>
        </div>
      </div>
      
      {/* Menu Button */}
      <button
        className="sm:hidden fixed top-4 left-4 bg-gray-700 text-white rounded-full h-10 w-10 flex items-center justify-center z-50"
        onClick={() => setShowMenu(prev => !prev)}
      >
        {showMenu ? 'X' : '☰'}
      </button>
    </>
  );
};

export default NavigationMenu;
